//
//TE Version 0.3.0
//

import { useEffect } from 'react'

import { withRouter } from 'react-router-dom'

const ScrollToTop = (props) => {
	const { location, children } = props

	useEffect(() => {
		window.scrollTo(0, 0)
	}, [location])

	return children
}

export default withRouter(ScrollToTop)
