import styled from 'styled-components'
import { FaUpload, FaRegWindowClose } from 'react-icons/fa'

import TEButton from './../TEButton'

export const ImageRowWrapper = styled.div`
	${(props) => {
		return `
			overflow-x: auto;
			width: 100%;
			padding: 5px;
			display: flex;
			align-items: center;
		`
	}}
`
export const ImageWrapper = styled.div`
	${(props) => {
		const { theme } = props
		return `
			position: relative;
			width: 150px;
			height: 150px;
			margin-left: 0px;
			margin-right: 15px;
			border: 1px solid ${theme.lightGray};
			display: flex;
			align-items: center;
			justify-content: center;
			border-radius: 5px;
			flexShrink: 0;
		`
	}}
`
export const Image = styled.img`
	${(props) => {
		return `
			display: block;
			width: 100%;
			object-fit: contain;
			border-radius: 5px;
		`
	}}
`
export const ClearImageButton = styled(TEButton)`
	${(props) => {
		return `
			position: absolute;
			display: flex;
			justify-content: center;
			align-items: center;
			top: 3px;
			right: 3px;
			width: auto;
			padding: 5px;
			text-align: center;
		`
	}}
`
export const ClearImageButtonIcon = styled(FaRegWindowClose)`
	${(props) => {
		return `
			color: inherit;
			display: block;
			font-size: 14px;
		`
	}}
`
export const Label = styled.label`
	${(props) => {
		const { theme, disabled } = props
		return `
			position: relative;
			display: flex;
			align-items: center;
			justify-content: center;
			width: 150px;
			height: 150px;
			flex-shrink: 0;
			padding: 30px;
			margin-left: 0px;
			margin-right: 15px;
			background-color: ${theme.white};
			cursour: pointer;
			border: 1px solid ${disabled ? theme.lighterGray : theme.lightGray};
			box-shadow: none;
			border-radius: 5px;
			transition: border-color 0.2s ease-in, box-shadow 0.2s ease-in;

			:hover {
				border: 1px solid ${disabled ? theme.lighterGray : theme.primary};
				boxShadow: ${disabled ? 'none' : `0 0 0 1px ${theme.primary} inset;`}
			}
			:active {
				border: 1px solid ${disabled ? theme.lighterGray : theme.primary};
				boxShadow: ${disabled ? 'none' : `0 0 0 1px ${theme.primary} inset;`}
			}
		`
	}}
`
export const UploadIcon = styled(FaUpload)`
	${(props) => {
		const { theme } = props
		return `
			font-size: 60px;
			color: ${theme.lightGray};
		`
	}}
`
export const Input = styled.input`
	${(props) => {
		return `
			display: none;
		`
	}}
`
export const ErrorMessage = styled.span`
	${(props) => {
		const { theme } = props
		return `
			display: block;
			padding-top: 5px;
			padding-bottom: 5px;
			font-size: 14px;
			color: ${theme.errorRed};
		`
	}}
`
