    ${(props) => {
        return `
            width: 100%;
            display: flex;
            position: relative;
            margin-bottom: 30px;

            @media (max-width: 500px) {
                margin-bottom: 15;
            }
        `
    }}
`
export const StepBar = styled.div`
    ${(props) => {
        const { stepCount, theme } = props
        return `
            position: absolute;
            top: 14px;
            background-color: ${theme.primary};
            height: 2px;
            width: ${100 - 100 / stepCount}%;
            left: ${100 / stepCount / 2}%;
        `
    }}
`
export const StepWrapper = styled.div`
    ${(props) => {
        const { stepCount } = props
        return `
            display: flex;
            flex-direction: column;
            align-items: center;
            width: ${100 / stepCount}%
        `
    }}
`
export const StepNumber = styled.div`
    ${(props) => {
        const { active, theme } = props
        return `
            text-align: center;
            width: 30px;
            height: 30px;
            margin-bottom: 10px;
            line-height: 30px;
            border: 1px solid ${theme.primary};
            z-index: 1;
            background-color: ${active ? theme.primary : theme.white};
            color: ${active ? theme.white : theme.primary};
            transition: color 0.2s ease-in, background-color 0.2s ease-in;
        `
    }}
`
export const StepTitle = styled.span`
    ${(props) => {
        return `
            text-align: center;
            font-size: 18px;
            display: block;

            @media (max-width: 500px) {
                font-size: 16px;
            }
            @media (max-width: 350px) {
                font-size: 14px;
            }
        `
